import React, { useState } from "react";
import "./Login.css";

export default function LandingMessage() {
  const [register, setRegister] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });

  const [signUp, setSignUp] = useState(false)
  const [valid, setValid] = useState(false);


  // const signup = () => {
  //   setRegister(true);
  // };

  // const login = () => {
  //   setRegister(false);
  // };

  const handleFirstNameChange = (e) => {
    setRegister({...register, firstName:e.target.value});
  }

  const handleLastNameChange = (e) => {
    setRegister({...register, lastName:e.target.value});
  }

  const handleEmailChange = (e) => {
    setRegister({...register, email:e.target.value});
  }

  const handlePasswordChange = (e) => {
    setRegister({...register, password:e.target.value});
  }

  const handleSignUpChange = (e) => {
    e.preventDefault();
    if (register.firstName && register.lastName && register.email && register.password) {
      setValid(true);
    }
    setSignUp(true);
  }

  // const handleEmail = (e) => {
  //   setRegister({...register, email:e.target.value});
  // };

  return (
    <div className="container">
      {!register ? (
        <div>
          <h1> Welcome </h1>
          <p> Welcome back! We missed you... </p>
          <p>Please login to your account now. </p>

          <form className="login-form">
            <input type="text" placeholder="Email" />
            
            <input type="Password" placeholder="Password" />
            
            <button type="submit" className="log-in">
              
              log in
            </button>
          </form>

          <hr />

          <p> You don't have an account yet? </p>
          <button type="submit" className="sign-up" onClick={signUp}>
            register
          </button>
        </div>

      ) : (
        <div>
          {/* register */}
          {signUp && valid ? <div className="success-message"> Registration Successful </div>: null } 
          <h1> Welcome </h1>
          <p> You don't have an account yet? </p>
          <p> Let's create now! </p>
          <form className="reg-form" onSubmit={handleSignUpChange}>
            <input type="text" placeholder="First Name" value={register.firstName} 
            onChange={handleFirstNameChange}/>
            <p id="first-name-error"> Please enter your first name</p>
            <input type="text" placeholder="Last Name" value={register.lastName}
            onChange={handleLastNameChange}/>
            <span id="last-name-error"> Please enter your last name</span>
            <input type="email" placeholder="Email" className="input" value={register.email}
            onChange={handleEmailChange} />
            <span id="email-error"> Please enter your email</span>
            <input type="Password" placeholder="Password" className="input" value={register.password}
            onChange={handlePasswordChange} />
            <br />
            <span id="password-error"> Please enter your password</span>
            <button type="submit" className="sign-up">
              register
            </button>
          </form>
          <hr />
          <p> You don't have an account yet? </p>
          <button type="submit" className="log-in" onClick={signUp}>
            log in
          </button>
        </div>
      )}
    </div>
  );
}
